import { Store, createStore, compose, applyMiddleware } from 'redux';
import reduxThunk from 'redux-thunk'; //whaz this again?
import logger from 'redux-logger'; // Whaz this again?
import { state } from './reducers'; //referring to module package?

const a: any = window; // establish the window for display, your browser
const composeEnhancers = a.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose; //eh?

const enhancer = composeEnhancers( // EH?!
    applyMiddleware(reduxThunk, logger),
    // other store enhancers if any
);

export const store: Store<any> = createStore( //has variables of state and enhancer... EHHHHH!??!?!?
    state,
    enhancer
);
